try {
	if(me.PTCSCUseMachineCode===false){
    	// use Math.random() to determine if utilization state should change and what it should change to
        var dateValue = new Date();
        var ToBeChanged = false;
        var NewUtilState = "N/A";

        if (me.PTCSCUtilizationState=="Down"){
            var ChangeFactor = Math.random();
            if (ChangeFactor >= 0.5){
                ToBeChanged = true;
                NewUtilState = "Running";}
        }
        else if (me.PTCSCUtilizationState=="Idle"){
            var ChangeFactor = Math.random();
            if (ChangeFactor >= 0.75){
                ToBeChanged = true;
                NewUtilState = "Running";}
        }
        else if (me.PTCSCUtilizationState=="Setup"){
            var ChangeFactor = Math.random();
            if (ChangeFactor >= 0.95){
                ToBeChanged = true;
                NewUtilState = "Running";}
        }
        else if (me.PTCSCUtilizationState=="Unavailable"){
            var ChangeFactor = Math.random();
            if (ChangeFactor >= 0.98){
                ToBeChanged = true;
                NewUtilState = "Setup";}
        }
        else if (me.PTCSCUtilizationState=="Running"){
            var ChangeFactor = Math.random();
            if (ChangeFactor >= 0.90){
                ToBeChanged = true;
                var NewStateFactor = Math.random();
                if (NewStateFactor < 0.5){
                    NewUtilState = "Down";}
                else if (NewStateFactor < 0.80){
                    NewUtilState = "Idle";}
                else if (NewStateFactor < 0.98){
                    NewUtilState = "Setup";}
                else {NewUtilState = "Unavailable";}
                }
        }
        else{
            ToBeChanged = true;
            NewUtilState = "Running"}

        // if the utilization state is to change, set the new reason group and utilization state first and then set the reason which triggers a stream write
        if (ToBeChanged == true){
            me.PTCSCEventStart = dateValue;
            if (NewUtilState == "Running"){
                me.PTCSCReasonGroup = "Running";
                me.PTCSCUtilizationState = NewUtilState;
                me.PTCSCReason = "Good Production";
            }
            else if (NewUtilState == "Unavailable"){
                me.PTCSCReasonGroup = "Unavailable";
                me.PTCSCUtilizationState = NewUtilState;
                var NewReasonFactor = Math.random();
                if (NewReasonFactor < 0.75){
                    me.PTCSCReason = "Meetings";}
                else {me.PTCSCReason = "Scheduled Off";}
            }
            else if (NewUtilState == "Setup"){
                me.PTCSCReasonGroup = "Setup";
                me.PTCSCUtilizationState = NewUtilState;
                var NewReasonFactor = Math.random();
                if (NewReasonFactor < 0.60){
                    me.PTCSCReason = "Order Change";}
                else if (NewReasonFactor < 0.95){
                    me.PTCSCReason = "Part Change";}
                else {me.PTCSCReason = "Die Change";}
            }
            else if (NewUtilState == "Idle"){
                me.PTCSCReasonGroup = "Idle";
                me.PTCSCUtilizationState = NewUtilState;
                var NewReasonFactor = Math.random();
                if (NewReasonFactor < 0.34){
                    me.PTCSCReason = "No Material";}
                else if (NewReasonFactor < 0.67){
                    me.PTCSCReason = "No Operator";}
                else {me.PTCSCReason = "Waiting on QC";}
            }
            else {
                me.PTCSCUtilizationState = "Down";
                var NewReasonFactor = Math.random();
                if (NewReasonFactor < 0.50){
                    me.PTCSCReasonGroup = "Downtime";
                    me.PTCSCReason = "Unknown";}
                else if (NewReasonFactor < 0.60){
                    me.PTCSCReasonGroup = "Electrical";
                    me.PTCSCReason = "Electrical Fault";}
                else if (NewReasonFactor < 0.70){
                    me.PTCSCReasonGroup = "Mechanical";
                    me.PTCSCReason = "Conveyor Jam";}
                else if (NewReasonFactor < 0.80){
                    me.PTCSCReasonGroup = "Mechanical";
                    me.PTCSCReason = "Feed Issues";}
                else if (NewReasonFactor < 0.90){
                    me.PTCSCReasonGroup = "Mechanical";
                    me.PTCSCReason = "Tool Adjustment";}
                else {me.PTCSCReasonGroup = "Misc";
                    me.PTCSCReason = "Cleaning";}
            }
        }  
	}
    else{
    	// use Math.random() to determine if machine code should change and what it should change to
        var dateValue = new Date();
        var ToBeChanged = false;
        var NewUtilState = "N/A";

        if (me.PTCSCUtilizationState=="Down"){
            var ChangeFactor = Math.random();
            if (ChangeFactor >= 0.5){
                ToBeChanged = true;
                NewUtilState = "Running";}
        }
        else if (me.PTCSCUtilizationState=="Idle"){
            var ChangeFactor = Math.random();
            if (ChangeFactor >= 0.75){
                ToBeChanged = true;
                NewUtilState = "Running";}
        }
        else if (me.PTCSCUtilizationState=="Setup"){
            var ChangeFactor = Math.random();
            if (ChangeFactor >= 0.95){
                ToBeChanged = true;
                NewUtilState = "Running";}
        }
        else if (me.PTCSCUtilizationState=="Unavailable"){
            var ChangeFactor = Math.random();
            if (ChangeFactor >= 0.98){
                ToBeChanged = true;
                NewUtilState = "Setup";}
        }
        else if (me.PTCSCUtilizationState=="Running"){
            var ChangeFactor = Math.random();
            if (ChangeFactor >= 0.90){
                ToBeChanged = true;
                var NewStateFactor = Math.random();
                if (NewStateFactor < 0.5){
                    NewUtilState = "Down";}
                else if (NewStateFactor < 0.80){
                    NewUtilState = "Idle";}
                else if (NewStateFactor < 0.98){
                    NewUtilState = "Setup";}
                else {NewUtilState = "Unavailable";}
                }
        }
        else{
            ToBeChanged = true;
            NewUtilState = "Running"}

        // if the utilization state is to change, set the new reason group and utilization state first and then set the reason which triggers a stream write
        if (ToBeChanged == true){
            me.PTCSCEventStart = dateValue;
            if (NewUtilState == "Running"){
				me.PTCSCMachineCode = 1009;
            }
            else if (NewUtilState == "Unavailable"){
                var NewReasonFactor = Math.random();
                if (NewReasonFactor < 0.75){
                    me.PTCSCMachineCode = 1014;}
                else {me.PTCSCMachineCode = 1015;}
            }
            else if (NewUtilState == "Setup"){
                me.PTCSCReasonGroup = "Setup";
                me.PTCSCUtilizationState = NewUtilState;
                var NewReasonFactor = Math.random();
                if (NewReasonFactor < 0.60){
                    me.PTCSCMachineCode = 1011;}
                else if (NewReasonFactor < 0.95){
                    me.PTCSCMachineCode = 1012;}
                else {me.PTCSCMachineCode = 1010;}
            }
            else if (NewUtilState == "Idle"){
                var NewReasonFactor = Math.random();
                if (NewReasonFactor < 0.34){
                   me.PTCSCMachineCode = 1006;}
                else if (NewReasonFactor < 0.67){
                    me.PTCSCMachineCode = 1007;}
                else {me.PTCSCMachineCode = 1008;}
            }
            else {
                me.PTCSCUtilizationState = "Down";
                var NewReasonFactor = Math.random();
                if (NewReasonFactor < 0.50){
                    me.PTCSCMachineCode = 1000;}
                else if (NewReasonFactor < 0.60){
                    me.PTCSCMachineCode = 1001;}
                else if (NewReasonFactor < 0.70){
                    me.PTCSCMachineCode = 1002;}
                else if (NewReasonFactor < 0.80){
                    me.PTCSCMachineCode = 1003;}
                else if (NewReasonFactor < 0.90){
                    me.PTCSCMachineCode = 1004;}
                else {me.PTCSCMachineCode = 1005;}
            }
        }          
    }
} catch (err) {
	logger.error(me.name + " Service UpdatePTCSCSimulatedProperties error at line " + err.lineNumber + ": " + err);  
}

if (me.PTCSCUseMachineCode == true){
    
    let managerThing = me.GetConfiguredManagerForIdentifier({identifier: "PTCSC.Utilization.Manager"});
    // result: INFOTABLE dataShape: "PTCSC.Utilization.MachineCode"
    let result = Things[managerThing].GetMachineCodeRecord({
        MachineCode: me.PTCSCMachineCode /* INTEGER */,
        EquipmentID: me.name /* STRING */
    });    
    var tableLength = result.rows.length;
    if (tableLength > 0){
		var row = result.rows[0];
        me.PTCSCReasonGroup = row.ReasonGroup;
        me.PTCSCUtilizationState = row.UtilizationState;
        me.PTCSCReason = row.Reason;
    }
}

// dateValue:DATETIME
var dateValue = new Date();

let managerThing = me.GetConfiguredManagerForIdentifier({identifier: "PTCSC.Utilization.Manager"});

Things[managerThing].AddUtilizationRecord({
	EquipmentID: me.name /* STRING */,
	ShiftID: me.PTCSCShiftID /* STRING */,
	ProductID: me.PTCSCProductID /* STRING */,
	UtilizationState: me.PTCSCUtilizationState /* STRING */,
	OperatorID: me.PTCSCOperatorID /* STRING */,
	ReasonGroup: me.PTCSCReasonGroup /* STRING */,
	Timestamp: dateValue /* DATETIME */,
	Reason: me.PTCSCReason /* STRING */
});
