// Create tokens for the menu entries
LocalizationTables["Default"].AddOrUpdateToken({
name: "PTCSC.UtilizationUI.Main.Token" /* STRING */,
value: "Utilization Overview" /* STRING */
});

LocalizationTables["Default"].AddOrUpdateToken({
name: "PTCSC.UtilizationUI.Machine.Token" /* STRING */,
value: "Machine Utilization" /* STRING */
});

// Return true as a default
var result = true;

let managerThing = me.GetConfiguredManagerForIdentifier({identifier: "PTCSC.Utilization.Manager"});

Things[managerThing].AddOrUpdateMachineCode({
	MachineCode: MachineCode /* INTEGER */,
	EquipmentID: EquipmentID /* STRING */,
	UtilizationState: UtilizationState /* STRING */,
	ReasonGroup: ReasonGroup /* STRING */,
	Reason: Reason /* STRING */
});

let managerThing = me.GetConfiguredManagerForIdentifier({identifier: "PTCSC.Utilization.Manager"});

Things[managerThing].AddOrUpdateReason({
	ReasonDescription: ReasonDescription /* STRING */,
	UtilizationState: UtilizationState /* STRING */,
	ReasonGroup: ReasonGroup /* STRING */,
	Failure: Failure /* BOOLEAN */,
	Reason: Reason /* STRING */
});

let managerThing = me.GetConfiguredManagerForIdentifier({identifier: "PTCSC.Utilization.Manager"});

Things[managerThing].AddOrUpdateReasonGroup({
	ParentReasonGroup: ParentReasonGroup /* STRING */,
	ReasonGroupDescription: ReasonGroupDescription /* STRING */,
	ReasonGroup: ReasonGroup /* STRING */
});

let managerThing = me.GetConfiguredManagerForIdentifier({identifier: "PTCSC.Utilization.Manager"});

Things[managerThing].AddOrUpdateUtilizationState({
	UtilizationStateDescription: UtilizationStateDescription /* STRING */,
	Utilized: Utilized /* BOOLEAN */,
	UtilizationState: UtilizationState /* STRING */,
	IncludeInUtilizationCalculation: IncludeInUtilizationCalculation /* BOOLEAN */
});

let managerThing = me.GetConfiguredManagerForIdentifier({identifier: "PTCSC.Utilization.Manager"});

Things[managerThing].DeleteMachineCode({
	MachineCode: MachineCode /* INTEGER */,
	EquipmentID: EquipmentID /* STRING */
});

let managerThing = me.GetConfiguredManagerForIdentifier({identifier: "PTCSC.Utilization.Manager"});

Things[managerThing].DeleteReason({
	Reason: Reason /* STRING */
});

let managerThing = me.GetConfiguredManagerForIdentifier({identifier: "PTCSC.Utilization.Manager"});

Things[managerThing].DeleteReasonGroup({
	ReasonGroup: ReasonGroup /* STRING */
});

let managerThing = me.GetConfiguredManagerForIdentifier({identifier: "PTCSC.Utilization.Manager"});

Things[managerThing].DeleteUtilizationState({
	UtilizationState: UtilizationState /* STRING */
});

let managerThing = me.GetConfiguredManagerForIdentifier({identifier: "PTCSC.Utilization.Manager"});

// result: INFOTABLE dataShape: "PTCSC.Utilization.UtilizationWithDuration"
let UtilizationRecords = Things[managerThing].GetUtilizationRecords({
	StartDate: StartDate /* DATETIME */,
	EndDate: EndDate /* DATETIME */
});

// result: INFOTABLE dataShape: "PTCSC.Utilization.Aggregate"
let result = Things[managerThing].GetAggregatesByUtilizationState({
	UtilizationRecords: UtilizationRecords /* INFOTABLE {"dataShape":"PTCSC.Utilization.UtilizationWithDuration"} */
});

let managerThing = me.GetConfiguredManagerForIdentifier({identifier: "PTCSC.Utilization.Manager"});

// result: INFOTABLE dataShape: "PTCSC.Utilization.UtilizationAndStatistics"
let result = Things[managerThing].GetFilteredUtilizationDataAndStats({
	StartDate: StartDate /* DATETIME */,
	EquipmentID: EquipmentID /* STRING */,
	UtilizationState: UtilizationState /* STRING */,
	EndDate: EndDate /* DATETIME */
});

let managerThing = me.GetConfiguredManagerForIdentifier({identifier: "PTCSC.Utilization.Manager"});

// result: INFOTABLE dataShape: "PTCSC.Utilization.MachineCode"
let result = Things[managerThing].GetMachineCodesByEquipment({
	EquipmentID: EquipmentID /* STRING */
});

let managerThing = me.GetConfiguredManagerForIdentifier({identifier: "PTCSC.Utilization.Manager"});

// result: INFOTABLE dataShape: "PTCSC.Utilization.MachineStartEndDates"
let result = Things[managerThing].GetMachineListingWithDates({
	StartDate: StartDate /* DATETIME */,
	EndDate: EndDate /* DATETIME */
});

let managerThing = me.GetConfiguredManagerForIdentifier({identifier: "PTCSC.Utilization.Manager"});

// result: INFOTABLE dataShape: "PTCSC.Utilization.MachineListingWIthProperties"
let result = Things[managerThing].GetMachineListingWithProperties();

let managerThing = me.GetConfiguredManagerForIdentifier({identifier: "PTCSC.Utilization.Manager"});

// result: INFOTABLE dataShape: "PTCSC.Utilization.ReasonGroup"
let result = Things[managerThing].GetReasonGroups();

let managerThing = me.GetConfiguredManagerForIdentifier({identifier: "PTCSC.Utilization.Manager"});

// result: INFOTABLE dataShape: "PTCSC.Utilization.Reason"
let result = Things[managerThing].GetReasonsByReasonGroup({
	ReasonGroup: ReasonGroup /* STRING */
});

let managerThing = me.GetConfiguredManagerForIdentifier({identifier: "PTCSC.Utilization.Manager"});

// result: INFOTABLE dataShape: "PTCSC.Utilization.StartEndDates"
let result = Things[managerThing].GetStartEndDates({
	HoursBack: HoursBack /* INTEGER {"defaultValue":8} */
});

let managerThing = me.GetConfiguredManagerForIdentifier({identifier: "PTCSC.Utilization.Manager"});

// result: INFOTABLE dataShape: "PTCSC.Utilization.UtilizationState"
let result = Things[managerThing].GetUtilizationStates();

